import React, { useEffect } from 'react';
import "./reactionTest.js"

var num = Math.ceil(Math.random()*5);
var flag = 0;
var beginTime = 0;
var endTime = 0;

function ChangeColor() {
    if(flag == 1){      
            endTime = +new Date();
            document.getElementById("reactionB").style.background="#57F15C";
            document.getElementById("reactionB").innerHTML=((endTime - beginTime) + " ms");
            // Send "reactionB" value to the backend
            var xhr = new XMLHttpRequest();
            xhr.open("POST", "/handle-reaction-b", true);
            xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
            xhr.send("reactionB=" + encodeURIComponent(document.getElementById("reactionB").innerHTML));
        }

    else if(flag == 0){
        document.getElementById("reactionB").style.background="#57F15C";
        document.getElementById("reactionB").innerHTML="Wait...";
        setTimeout(function(){
            document.getElementById("reactionB").style.background="#E0457B";
            document.getElementById("reactionB").innerHTML="Click!";
            beginTime = +new Date();
        },num*1000+1000);
    }

    else if(flag == 2){
        location.reload();
    }

    flag++;
}

function ReactionTest() {

    return (
        <div className="reactionTest">
            <div id="reaction_button" class="container">
                <p>           
                    <button onclick={ ChangeColor } id="reactionB">Click Here</button>
                </p>
                <p>You can refresh current page by clicking on the green area again after the test :)</p>
            </div>

            <div id="rank" class="container">
                <div if="rank_title">
                    <p><label id="rank_title">Rank</label></p>
                </div>
                <table>
                    {/* TODO: Link to the database and fetch data */}
                    {/* {{ range $element := .rank }}
                        <tr style="left: 30%; text-align: center">
                            <td>{{$element.id}}</td>
                            <td>{{$element.userName}}</td>
                            <td>{{$element.score}} ms</td>
                        </tr>
                    {{ end }} */}
                </table>
            </div>
        </div>
    );
}

export default ReactionTest;
