import React, { useState } from "react";
import "./login.css";
import Header from "../../components/header/header.js";
import Footer from "../../components/footer/footer.js";
import ErrorBox from "../../components/errorBox/errorBox.js";

function Login() {
    const [user, setUser] = useState("");
    const [pwd, setPwd] = useState("");
    const [message, setMessage] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch("/api/login_process", {
            method: "POST",
            headers: {
                "Content-Type": "application/x-www-form-urlencoded",
            },
            body: new URLSearchParams({
                user: user,
                pwd: pwd
            }),
        });

        const data = await response.json();
        if (response.ok) {
            setMessage(`Login successful! Token: ${data.token}`);
        } else {
            setMessage(data.message || "Login failed");
        }
    };

    return (
        <div className="login">
            {/* Import Header */}
            <div>
                <Header />
            </div>

            <div className="frame">
                <div className='login_sub'>
                    <div className='title'>Login</div>
                    <div className='biao'>
                        <form id="myForm" onSubmit={handleSubmit}>
                            <div id="boxs">
                                <div className="box1">
                                    <label>Username: </label>
                                    <label>Password: </label>
                                </div>
                                <div className="box2">
                                    <input
                                        type="text"
                                        name="user"
                                        value={user}
                                        onChange={(e) => setUser(e.target.value)}
                                    />
                                    <input
                                        type="password"
                                        name="pwd"
                                        value={pwd}
                                        onChange={(e) => setPwd(e.target.value)}
                                    />
                                </div>
                            </div>
                            <div className="container2">
                                <button type="submit" className="button">
                                    <div className="button__line"></div>
                                    <div className="button__line"></div>
                                    <span className="button__text">Login</span>
                                </button>
                                <p><a href="/register" id="go_to_register">I don't have an account. I want to sign up!</a></p>
                            </div>
                        </form>
                    </div>
                </div>
            </div>

            {/* Import Footer */}
            <div>
                <Footer />
            </div>

            {/* Import errorBox */}
            <div id="error_box">
                {<ErrorBox message={message} />}
            </div>
        </div>
    );
}

export default Login;
